//foncion qui permer de donnez la fonctionnalité a la button modifier
    private void buttonModifierActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_buttonModifierActionPerformed
        String id=txtId.getText();
        String name=txtNom.getText();
        String MobileNumber=txtNumberTel.getText();
        String email=txtEmail.getText();
        String gender=txtSexe.getText();
        String fathername=txtNomPere.getText();
        String mathername=txtNomMere.getText();
        String HeurSport=txtHeur.getText();
        String age =txtAge.getText();
        String montant=txtMontant.getText();
        try{
            Connection con =ConnexionDB.getConnection();
            PreparedStatement ps=con.prepareStatement("Update member set name =?,email=?,mobilenumber=?,gender =?,fathername=?,mothername=?,gymtime=?,age=?,amount=?");
            ps.setString(1,name);
            ps.setString(2,email);
            ps.setString(3,MobileNumber);
            ps.setString(4,gender);
            ps.setString(5,fathername);
            ps.setString(6,mathername);
            ps.setString(7,HeurSport);
            ps.setString(8,age);
            ps.setString(9,montant);
            ps.executeUpdate();
            JOptionPane.showMessageDialog(null,"modifié avec succès");
            setVisible(false);
            new UpdateDeletsMember().setVisible(true);
        }catch(Exception e){
            JOptionPane.showMessageDialog(null,e);
        }
    }//GEN-LAST:event_buttonModifierActionPerformed

