    private void buttonRechercherActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_buttonRechercherActionPerformed
       int checkid=0; 
       String id=txtId.getText();
       try{
           Connection con =ConnexionDB.getConnection();
          Statement st=con.createStatement();
          ResultSet rs=st.executeQuery("select * from member where id ="+id+"");
          while(rs.next())
          {
              checkid=1 ;
              txtId.setEditable(false);
              txtNom.setText(rs.getString(2));
              txtNumberTel.setText(rs.getString(3));
              txtEmail.setText(rs.getString(4));
              txtSexe.setText(rs.getString(5));
              txtSexe.setEditable(false);
              txtNomPere.setText(rs.getString(6));
              txtNomMere.setText(rs.getString(7));
              txtHeur.setText(rs.getString(8));
              txtHeur.setEditable(false);            
              txtAge.setText(rs.getString(9));
              txtMontant.setText(rs.getString(10));
          }
          if(checkid==0){
              JOptionPane.showMessageDialog(null, "le nombre d'Id que vous avez tapez n'existe pas !");
          }
       }catch(Exception e){
           JOptionPane.showMessageDialog(null,e);
       }
    }//GEN-LAST:event_buttonRechercherActionPerformed

