    private void txtSupprimerActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_txtSupprimerActionPerformed
        int a=JOptionPane.showConfirmDialog(null,"vous etes sur de supprimer ce contact ","choix ",JOptionPane.YES_NO_OPTION);
        if(a==0){
            String id=txtId.getText();
            try{
                Connection con=ConnexionDB.getConnection();
                Statement st=con.createStatement();
                st.execute("delete from member where id ="+id+"");
                JOptionPane.showMessageDialog(null,"Suprimer avec succès ");
                setVisible(false);
                new UpdateDeletsMember().setVisible(true);
                
            }catch(Exception e){
                JOptionPane.showMessageDialog(null, e);
            }
        }
    }//GEN-LAST:event_txtSupprimerActionPerformed

